
// set up an object for categories and words
var beerBrands = [
    "Boulevard",
    "KC Bier Co",
    "Lagunitas",
    "Founders",
    "Martin City Brewing",
    "Dogfish Head",
    "Great Divide Brewing Co",
    "Coors",
    "Bud Light",
    "Natural Light"
    ];

//set up initial wins, guesses, and losses
var wins = 0;
var guessesLeft = 7;
var losses = 0;

var wrong;

//undefined variable for random word generated by button on the page
var randomWord = null;

//set up an array for letters guessed
var alreadyGuessed = [];

var userGuess;
//set up an array for the underscores.
var wordBlanks = [];

var currentWord = document.getElementById("currentWord").innerHTML;

document.getElementById("newWordBtn").addEventListener("click", function(){
    document.getElementById("currentWord").innerHTML = "";
    document.getElementById("wrongGuesses").innerHTML = "WRONG GUESSES: ";
    alreadyGuessed = [];
    wordBlanks = [];
    randomWord = beerBrands[Math.floor(Math.random() * beerBrands.length)];

    for(i = 0; i < randomWord.length; i++){
        wordBlanks.push("_ ");
    }
    document.getElementById("currentWord").innerHTML = wordBlanks.join("");
});

function reset(){
    document.getElementById("currentWord").innerHTML = "";
    document.getElementById("wrongGuesses").innerHTML = "WRONG GUESSES: ";
    document.getElementById("wins").innerHTML = "Wins: " + wins;
    document.getElementById("losses").innerHTML = "Losses: " + losses;

    guessesLeft = 7;
    alreadyGuessed = [];
    wordBlanks = [];
    randomWord = beerBrands[Math.floor(Math.random() * beerBrands.length)];

    for(i = 0; i < randomWord.length; i++){
        wordBlanks.push("_ ");
    }
    document.getElementById("currentWord").innerHTML = wordBlanks.join("");
};

function game(){
    document.onkeyup = function(event){

        userGuess = event.key;

        for(i=0; i < randomWord.length; i++){

            if(userGuess === randomWord[i]){
                wordBlanks[i] = userGuess;
                currentWord = document.getElementById("currentWord").innerHTML = wordBlanks.join("");
            }
            else{
                //do nothing
            }
        }

        if(! randomWord.includes(userGuess)){
            if( ! alreadyGuessed.includes(userGuess)){
                alreadyGuessed.push(userGuess);
                document.getElementById("wrongGuesses").innerHTML = "WRONG GUESSES: <br>" + alreadyGuessed;
                guessesLeft--;
            }
        }

        if(randomWord === currentWord){
            alert("You win: the word was " + randomWord);
            wins++;
            reset();
        }

        if(guessesLeft === 0){
            alert("You LOSE lololol");
            losses++;
            reset();
        }

    };

};


game();







